package org.ArgentumOnline.server.aStar;import java.util.ArrayList;import java.util.List;public class PQ {    List<Node> q = new ArrayList<Node>();        public Node pop() {        Node node = this.q.get(0);        this.q.remove(node);        return node;    }        public void add(Node node) {        if(this.q.size() == 0) {            this.q.add(node);        } else {            int i;            for(i = 0; i < this.q.size(); i++) {                Node holder = this.q.get(i);                if(holder.totalCost >= node.totalCost) {                    this.q.add(i, node);                    break;                }            }            if(i == this.q.size()) {                this.q.add(node);            }        }    }        public boolean contains(Node node) {        return this.q.contains(node);    }        public void remove(Node node) {        this.q.remove(node);    }        public int size() {        return this.q.size();    }}    